import { Layout, Menu, Breadcrumb } from 'antd';
import {
	DesktopOutlined,
	PieChartOutlined,
	FileOutlined,
	TeamOutlined,
	UserOutlined,
} from '@ant-design/icons';
import React, { useState } from 'react';
import { Switch, Route, useRouteMatch, Link } from 'react-router-dom';
import CourseList from '../course/List';
import './Dashboard.css';
import User from '../course/User';
const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

const TeacherDashboard = (props) => {
	const [collapsed, setCollapsed] = useState(false);
	const { path } = useRouteMatch;
	const onCollapse = (collapsed) => {
		setCollapsed({ collapsed });
	};
	const onSelect = (url) => {
		<Link to={url}></Link>;
	};
	return (
		<>
			<Switch></Switch>
			<Layout style={{ minHeight: '100vh' }}>
				<Sider collapsible collapsed={collapsed} onCollapse={onCollapse}>
					<div className='logo' />
					<Menu theme='dark' defaultSelectedKeys={['1']} mode='inline'>
						<Menu.Item key='course' title='Kurs' icon={<PieChartOutlined />}>
							<Link to='/course'>Kurs</Link>
						</Menu.Item>
						<Menu.Item key='student' icon={<DesktopOutlined />}>
							Öğrenciler
						</Menu.Item>
						<Menu.Item key='user' title='kullanıcı' icon={<UserOutlined />}>
							<Link to='/user'>kullanıcı</Link>
						</Menu.Item>
					</Menu>
				</Sider>
				<Layout className='site-layout'>
					<Header className='site-layout-background' style={{ padding: 0 }} />

					<Content style={{ margin: '0 16px' }}>
						<div
							className='site-layout-background'
							style={{ padding: 24, minHeight: 360 }}
						>
							<Route path={`${path}/course`}>
								<CourseList />
							</Route>
							<Route path={`${path}/user`}>
								<User />
							</Route>
						</div>
					</Content>
					<Footer style={{ textAlign: 'center' }}>
						Examination System ©2020 Created by Hatice Durmaz
					</Footer>
				</Layout>
			</Layout>
		</>
	);
};
export default TeacherDashboard;
