import { Form, Input, Button, Space, DatePicker, Card } from 'antd';
import { MinusCircleOutlined, PlusOutlined } from '@ant-design/icons';
const { RangePicker } = DatePicker;
const CreateExam = () => {
	const onFinish = (values) => {
		console.log('Received values of form:', values);
	};

	return (
		<Form name='dynamic_form_nest_item' onFinish={onFinish} autoComplete='off'>
			<Space style={{ display: 'grid', marginBottom: 8 }} align='baseline'>
				<RangePicker showTime={true} />

				<Form.List name='questions'>
					{(fields, { add, remove }) => (
						<>
							{fields.map((field) => (
								<Card size='default' title='Question' style={{ width: 1000 }}>
									<Space
										key={field.key}
										style={{ display: 'grid', marginBottom: 8 }}
										align='baseline'
									>
										<Form.Item
											{...field}
											name={[field.question, 'question']}
											fieldKey={[field.fieldKey, 'question']}
											rules={[{ required: true, message: 'Missing question' }]}
										>
											<Input placeholder='Question' />
										</Form.Item>

										<Form.List name='options'>
											{(fields, { add, remove }) => (
												<>
													{fields.map((field) => (
														<Card
															size='small'
															title='Option'
															style={{ width: 500 }}
														>
															<Space
																key={field.key}
																style={{ display: 'grid', marginBottom: 8 }}
																align='baseline'
															>
																<Form.Item
																	{...field}
																	name={[field.option, 'option']}
																	fieldKey={[field.fieldKey, 'option']}
																	rules={[
																		{
																			required: true,
																			message: 'Missing option',
																		},
																	]}
																>
																	<Input placeholder='Option' />
																</Form.Item>
																<MinusCircleOutlined
																	onClick={() => remove(field.name)}
																/>
															</Space>
														</Card>
													))}

													<Form.Item>
														<Button
															type='dashed'
															onClick={() => add(field.name)}
															block
															icon={<PlusOutlined />}
														>
															Add Option
														</Button>
													</Form.Item>
												</>
											)}
										</Form.List>
										<MinusCircleOutlined onClick={() => remove(field.name)} />
									</Space>
								</Card>
							))}
							<Form.Item style={{ width: 1000 }}>
								<Button
									type='dashed'
									onClick={() => add()}
									block
									icon={<PlusOutlined />}
								>
									Add Question
								</Button>
							</Form.Item>
						</>
					)}
				</Form.List>
			</Space>

			<Form.Item>
				<Button type='primary' htmlType='submit'>
					Submit
				</Button>
			</Form.Item>
		</Form>
	);
};
export default CreateExam;
