import { Form, Input, Button, Space, DatePicker, Card, Select } from 'antd';
import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { MinusCircleOutlined, PlusOutlined } from '@ant-design/icons';
const { RangePicker } = DatePicker;
const { Option } = Select;
const CreateExam = () => {
	const [disaable, setDisable] = useState(false);
	const { id } = useParams();
	const onFinish = (values) => {
		console.log('Received values of form:', values);
	};
	const onOk = (value) => {
		console.log(
			'selected time:',
			value[0].format('YYYY-MM-DD HH:mm'),
			value[1].format('YYYY-MM-DD HH:mm')
		);
	};
	const onClick = () => {
		setDisable(true);
	};
	return (
		<Form name='dynamic_form_nest_item' onFinish={onFinish} autoComplete='off'>
			<Space style={{ display: 'grid', marginBottom: 8 }} align='baseline'>
				<Card>
					<Form.Item
						name={'exam-name'}
						fieldKey={'exam-name'}
						label={'Exam Name'}
						rules={[{ required: true, message: 'Missing exam name' }]}
					>
						<Input />
					</Form.Item>
				</Card>
				<Card>
					<Form.Item
						name={'date-range'}
						fieldKey={'date-range'}
						label={'Date Interval'}
						rules={[{ required: true, message: 'Missing date range' }]}
					>
						<RangePicker
							showTime={{ format: 'HH:mm' }}
							format='YYYY-MM-DD HH:mm'
							onOk={onOk}
						/>
					</Form.Item>
				</Card>
			</Space>
			<Form.Item rules={[{ required: true, message: 'Missing question' }]}>
				<Input title='Question' placeholder='Question' />
				<Input title='A' placeholder='Option A' />
				<Input title='B' placeholder='Option B' />
				<Input title='C' placeholder='Option C' />
				<Input title='D' placeholder='Option D' />
				<Select>
					<Option value='A'>A</Option>
					<Option value='B'>B</Option>
					<Option value='C'>C</Option>
					<Option value='D'>D</Option>
				</Select>
			</Form.Item>

			<Form.Item>
				<Button onClick={onClick} type='primary' htmlType='submit'>
					Create
				</Button>
			</Form.Item>
		</Form>
	);
};
export default CreateExam;
